INSERT INTO `geopatrimoine`.`patrimoinehistorique` (`id`, `codePostal`, `commune`, `description`, `geoTag`, `horaires`, `lienImage`, `nom`, `tarif`) VALUES ('7', '60005', 'Lyon', '1er secteur sauvegardé de France, depuis 1964, et l’un des plus vastes ensembles Renaissance d’Europe (24 ha). Il reste de l\'époque médiévale le jardin archéologique vers la cathédrale, au bas des pentes de la colline de Fourvière.', '4.8169922,45.7612135', 'Ouvert tous les jours.', 'vieuxlyon.jpg', 'Le Quartier du Vieux Lyon', 'Gratuit');
INSERT INTO `geopatrimoine`.`patrimoinehistorique` (`id`, `codePostal`, `commune`, `description`, `geoTag`, `horaires`, `lienImage`, `nom`, `tarif`) VALUES ('8', '60005', 'Lyon', 'Les traboules, ces raccourcis en forme de passage intérieur, permettent de communiquer d\'une rue à l\'autre en traversant un ou plusieurs immeubles et cours. Elles recèlent des curiosités architecturales : escaliers à vis, façades et galeries.', '4.8273928,45.7670484', 'Ouvert tous les jours.', 'traboules.jpg', 'Les Traboules du Vieux Lyon', 'Gratuit');
INSERT INTO `geopatrimoine`.`patrimoinehistorique` (`id`, `adresse`, `codePostal`, `commune`, `description`, `geoTag`, `horaires`, `lienImage`, `nom`, `tarif`) VALUES ('9', '8 place Saint-Jean', '60005', 'Lyon', 'La cathédrale fut construite de 1180 à 1480, elle mesure 79 m de long, 26 m de large, 32 m de haut (voûte principale). Sa façade date du début du XIV° siècle. La grande rose de 1392, oeuvre de Jacques de Beaujeu mesure 8 m de diamètre.', '4.8251016,45.7608008', 'Ouvert toute l\'année de 8h15 à 19h (du lun. au ven. à 19h45).', 'cathedralesaintjean.jpg', 'Primatiale Cathédrale Saint-Jean-Baptiste', 'Gratuit');
INSERT INTO `geopatrimoine`.`patrimoinehistorique` (`id`, `adresse`, `codePostal`, `commune`, `description`, `geoTag`, `horaires`, `lienImage`, `nom`, `tarif`) VALUES ('10', 'Place des Terreaux ', '60001', 'Lyon', 'La place des Terreaux, sur laquelle est située la fontaine Bartholdi, a une superficie de 7000 m². Installée vers 1625 sur les anciens fossés des fortifications de la Lanterne, elle a été utilisée comme lieu d\'exécution capitale et comme marché aux porcs.', '4.8319232,45.7673441', 'Ouvert tous les jours.		', 'placedesterreuax.jpg', 'La Place des Terreaux', 'Gratuit');